import React, { useState } from "react";
import logo from './logo.svg';
import './App.css';
import { Home } from "./components/Home";
import { Login } from "./components/Login";
import { Sign_Up } from "./components/Sign_Up";
import { BrowserRouter as Router, Switch,Routes, 
  Route, Redirect,} from "react-router-dom";

function App() {
  const [currentForm, setCurrentForm] = useState('login');

  const toggleForm = (formName) => {
    setCurrentForm(formName);
  }

  return (
    <div className="App">
      <Router>
        <Switch>
          <Route exact path = "/" component = {<Home></Home>}/>

          <Route path = "/Login" component = {<Login></Login>}/>

          <Route path = "/Sign_Up" component = {<Sign_Up></Sign_Up>}/>
        </Switch>
      </Router>
      <img src="https://upload.wikimedia.org/wikipedia/tr/0/0a/TFF_logo.png" alt= ""></img>
     
      {
       
        currentForm === "login" ? <Login onFormSwitch={toggleForm}/> : <Sign_Up onFormSwitch={toggleForm}/>
      }
      <img src= "./public/images/rate12.png" alt=""></img>
    </div>
    
    
       
  );
  
}

export default App;
